INSERT INTO "tbarticle" VALUES ('68', '	Winston repaired th ', '5', '	Winston repaired the sink, taking out the unpleasant knot of hair that was stopping the water running away. He washed his hands and went back to the other room. ', '2015-11-22 00:00:00', 't', 'some image', '''repair'':2 ''th'':3 ''winston'':1', '''away'':18 ''back'':25 ''hair'':11 ''hand'':22 ''knot'':9 ''repair'':2 ''room'':29 ''run'':17 ''sink'':4 ''stop'':14 ''take'':5 ''unpleas'':8 ''wash'':20 ''water'':16 ''went'':24 ''winston'':1');
