INSERT INTO "tbarticle" VALUES ('71', '	''You''re a Eurasian  ', '4', '	''You''re a Eurasian spy!'' screamed the boy. ''You''re a thoughtcriminal! I''ll shoot you, I''ll vaporize you!'' ', '2015-11-22 00:00:00', 't', 'some image', '''eurasian'':4 ''re'':2', '''boy'':8 ''eurasian'':4 ''ll'':14,18 ''re'':2,10 ''scream'':6 ''shoot'':15 ''spi'':5 ''thoughtcrimin'':12 ''vapor'':19');
