INSERT INTO "tbarticle" VALUES ('75', '	Some Eurasian priso ', '3', '	Some Eurasian prisoners, guilty of war crimes against Oceania, were going to hang slowly in the park that evening. This happened every month or two and was a popular evening''s entertainment. Children were often taken to see it. ', '2015-11-22 00:00:00', 't', 'some image', '''eurasian'':2 ''priso'':3', '''children'':33 ''crime'':7 ''entertain'':32 ''eurasian'':2 ''even'':19,30 ''everi'':22 ''go'':11 ''guilti'':4 ''hang'':13 ''happen'':21 ''month'':23 ''oceania'':9 ''often'':35 ''park'':17 ''popular'':29 ''prison'':3 ''see'':38 ''slowli'':14 ''taken'':36 ''two'':25 ''war'':6');
