INSERT INTO "tbarticle" VALUES ('123', '	''Oh, Smith, old boy ', '1', '	''Oh, Smith, old boy,'' he said. ''I hear that son of mine threw stones at you yesterday. I talked to him about it. He won''t do it again, believe me.'' ', '2015-11-22 00:00:00', 't', 'some image', '''boy'':4 ''oh'':1 ''old'':3 ''smith'':2', '''believ'':30 ''boy'':4 ''hear'':8 ''mine'':12 ''oh'':1 ''old'':3 ''said'':6 ''smith'':2 ''son'':10 ''stone'':14 ''talk'':19 ''threw'':13 ''won'':25 ''yesterday'':17');
