INSERT INTO "tbarticle" VALUES ('551', '	He began to write,  ', '6', '	He began to write, with big child-like letters: ', '2015-11-22 00:00:00', 't', 'some image', '''began'':2 ''write'':4', '''began'':2 ''big'':6 ''child'':8 ''child-lik'':7 ''letter'':10 ''like'':9 ''write'':4');
