INSERT INTO "tbarticle" VALUES ('565', '	He had moved to one ', '1', '	He had moved to one side and Winston could now see what was on the table. It was a big metal box and through holes in the sides he could see movement. Rats. ', '2015-11-22 00:00:00', 't', 'some image', '''move'':3 ''one'':5', '''big'':20 ''box'':22 ''could'':9,30 ''hole'':25 ''metal'':21 ''move'':3 ''movement'':32 ''one'':5 ''rat'':33 ''see'':11,31 ''side'':6,28 ''tabl'':16 ''winston'':8');
