INSERT INTO "tbarticle" VALUES ('577', '	''They can''t get ins ', '3', '	''They can''t get inside you,'' she had said. But they could get inside you. And when they did, something inside you died. ', '2015-11-22 00:00:00', 't', 'some image', '''get'':4 ''in'':5', '''could'':12 ''die'':23 ''get'':4,13 ''insid'':5,14,21 ''said'':9 ''someth'':20');
INSERT INTO "tbarticle" VALUES ('578', '	He had seen her; he ', '4', '	He had seen her; he had even spoken to her. There was no danger in it. He knew that. They took no interest in him now. They could even see each other again if either of them wanted to. But they did not want to. ', '2015-11-22 00:00:00', 't', 'some image', '''seen'':3', '''could'':28 ''danger'':14 ''either'':35 ''even'':7,29 ''interest'':23 ''knew'':18 ''see'':30 ''seen'':3 ''spoken'':8 ''took'':21 ''want'':38,44');
