INSERT INTO "tbarticle" VALUES ('580', '	They sat down on tw ', '1', '	They sat down on two iron chairs, not too close together. There were no telescreens here but possibly hidden microphones. It did not matter. ', '2015-11-22 00:00:00', 't', 'some image', '''sat'':2 ''tw'':5', '''chair'':7 ''close'':10 ''hidden'':19 ''iron'':6 ''matter'':24 ''microphon'':20 ''possibl'':18 ''sat'':2 ''telescreen'':15 ''togeth'':11 ''two'':5');
