INSERT INTO "tbarticle" VALUES ('582', '	''I betrayed you, to ', '1', '	''I betrayed you, too,'' he said. ', '2015-11-22 00:00:00', 't', 'some image', '''betray'':2', '''betray'':2 ''said'':6');
INSERT INTO "tbarticle" VALUES ('583', '	''In the end they do ', '4', '	''In the end they do something so terrible that you say "Don''t do it to me, do it to somebody else, do it to the person I love." You only care about yourself.'' ', '2015-11-22 00:00:00', 't', 'some image', '''end'':3', '''care'':32 ''els'':22 ''end'':3 ''love'':29 ''person'':27 ''say'':11 ''somebodi'':21 ''someth'':6 ''terribl'':8');
